<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
  <title>Ploughing Tools</title>
  <style>
    body {
      font-family: Arial, sans-serif;
      background: #f8fafc;
      margin: 0;
      padding: 20px;
    }
    h1 {
      text-align: center;
      color: #2d3748;
    }
    .tabs {
      text-align: center;
      margin-bottom: 20px;
    }
    .tabs button {
      padding: 10px 20px;
      margin: 5px;
      border: none;
      background-color: #e2e8f0;
      cursor: pointer;
      border-radius: 8px;
    }
    .tabs button.active {
      background-color: #38a169;
      color: white;
    }
    .tools-container {
      display: flex;
      flex-wrap: wrap;
      gap: 20px;
      justify-content: center;
    }
    .card {
      background: white;
      padding: 20px;
      border-radius: 12px;
      box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
      width: 300px;
      text-align: center;
    }
    .card img {
      max-width: 100%;
      height: 160px;
      object-fit: cover;
      border-radius: 8px;
      margin-bottom: 10px;
    }
    .card h2 {
      margin-top: 0;
      color: #2f855a;
    }
    .price {
      font-size: 18px;
      color: #2b6cb0;
      margin: 10px 0;
    }
    .card button {
      width: 100%;
      padding: 10px;
      background-color: #38a169;
      color: white;
      border: none;
      border-radius: 8px;
      cursor: pointer;
    }
  </style>
</head>
<body>

  <h1>ðŸŒ¾ Ploughing Tools</h1>
  <p style="text-align:center;">Affordable tools available for purchase or rental</p>

  <div class="tabs">
    <button class="active" onclick="showTab('purchase')">Buy</button>
    <button onclick="showTab('rent')">Rent</button>
  </div>

  <div class="tools-container" id="tools"></div>

  <script>
    const tools = [
      { 
        name: "Traditional Plough", 
        use: "Manual ploughing for small fields", 
        purchase: "â‚¹2,000", 
        rent: "â‚¹50/day",
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRnel94SpJqssURP3U-ODlW8mUCUeTVfCHLVw&s" // Replace with actual image URL
      },
      { 
        name: "Mouldboard Plough", 
        use: "Used for turning over the soil", 
        purchase: "â‚¹15,000", 
        rent: "â‚¹300/day",
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR1qeSbm0vCapTSjwOx_wAVo92rdmNrZgysIw&s" // Replace with actual image URL
      },
      { 
        name: "Disc Plough", 
        use: "Suitable for hard and dry soil", 
        purchase: "â‚¹25,000", 
        rent: "â‚¹600/day",
        image: "data:image/jpeg;base64," // Replace with actual image URL
      },
      { 
        name: "Chisel Plough", 
        use: "Used for deep tillage", 
        purchase: "â‚¹20,000", 
        rent: "â‚¹500/day",
        image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQxCF4waeNLfZrclclOIvtbkWYvGworuB7eOg&s" // Replace with actual image URL
      },
      { 
        name: "Power Tiller", 
        use: "Motorized tool for ploughing and tilling", 
        purchase: "â‚¹80,000", 
        rent: "â‚¹1,000/day",
        image: "data:image/jpeg;base64," // Replace with actual image URL
      },
      { 
        name: "Rotary Plough", 
        use: "Used for mixing soil and preparing seedbeds", 
        purchase: "â‚¹50,000", 
        rent: "â‚¹800/day",
        image: "data:image/jpeg;base64," // Replace with actual image URL
      }
    ];

    let currentTab = 'purchase';

    function showTab(tab) {
      currentTab = tab;
      document.querySelectorAll('.tabs button').forEach(btn => btn.classList.remove('active'));
      event.target.classList.add('active');
      renderTools();
    }

    function renderTools() {
      const container = document.getElementById('tools');
      container.innerHTML = '';
      tools.forEach(tool => {
        const card = document.createElement('div');
        card.className = 'card';
        card.innerHTML = `
          <img src="${tool.image}" alt="${tool.name}" />
          <h2>${tool.name}</h2>
          <p>${tool.use}</p>
          <p class="price">${tool[currentTab]}</p>
          <button>${currentTab === 'purchase' ? 'Buy Now' : 'Rent Now'}</button>
        `;
        container.appendChild(card);
      });
    }

    renderTools();
  </script>

</body>
</html>